(function(){$("#programmatically-show-modal").on("click",function(){const o=document.querySelector("#programmatically-modal");tailwind.Modal.getOrCreateInstance(o).show()}),$("#programmatically-hide-modal").on("click",function(){const o=document.querySelector("#programmatically-modal");tailwind.Modal.getOrCreateInstance(o).hide()}),$("#programmatically-toggle-modal").on("click",function(){const o=document.querySelector("#programmatically-modal");tailwind.Modal.getOrCreateInstance(o).toggle()})})();
